import React, { useState } from "react";
import { OpenSeaPort, Network } from "opensea-js";
import Web3 from "web3";
import { osApiKey, networkName } from "../DataContext";
// import { connectedAddress } from "./Login";

const windowWeb3 = new Web3(window.ethereum);
const accounts = windowWeb3.eth.getAccounts();
const account = accounts[0];

export async function makeOffer(nftContractAddress, tokenId, offerAmount) {
  try {
    const provider = window.ethereum;
    await provider.enable();
    const web3 = new Web3(provider);
    const seaport = new OpenSeaPort(provider, {
      networkName: Network.Main,
      apiKey: osApiKey(),
    });

    const offer = {
      asset: {
        tokenId: tokenId,
        tokenAddress: nftContractAddress,
      },
      fromAddress: account,
      toAddress: "0x0000000000000000000000000000000000000000",
      value: Web3.utils.toWei(offerAmount.toString(), "ether"),
      startAmount: Web3.utils.toWei(offerAmount.toString(), "ether"),
      quantity: 1,
      expirationTime: Math.round(Date.now() / 1000 + 60 * 60 * 24 * 30),
    };

    const gasPrice = await web3.eth.getGasPrice();
    const estimatedGas = await seaport.api.estimateGas("fillOrder", offer);

    const transactionHash = await seaport.fulfillOrder({
      order: offer,
      accountAddress: account,
      feeMethod: 1,
      gasPrice: gasPrice,
      feeRecipientAddress: seaport._networkFeeRecipient,
      waitForConfirmation: true,
      estimatedGas: estimatedGas,
    });

    console.log(`Transaction hash: ${transactionHash}`);
    return transactionHash;
  } catch (error) {
    console.log(`Error making offer: ${error}`);
    return null;
  }
}

function Offer() {
  const [isMakingOffer, setIsMakingOffer] = useState(false);
  const [offerResult, setOfferResult] = useState("");

  async function handleMakeOffer(nftContractAddress, tokenId, offerAmount) {
    setIsMakingOffer(true);
    const transactionHash = await makeOffer(nftContractAddress, tokenId, offerAmount);
    setIsMakingOffer(false);

    if (transactionHash) {
      setOfferResult(`Offer successfully made. Transaction hash: ${transactionHash}`);
    } else {
      setOfferResult("Failed to make offer.");
    }
  }

  return (
    <div>
      {isMakingOffer && <p>Making offer...</p>}
      {offerResult && <p>{offerResult}</p>}
    </div>
  );
}

export function OfferAndSignature(nftContractAddress, tokenId, offerAmount) {
  return <Offer nftContractAddress={nftContractAddress} tokenId={tokenId} offerAmount={offerAmount} />;
}

export default Offer;
