import React, { useEffect, useState } from 'react';
import { createOffer } from './components/Offer';

function Form(props) {
  const [nftContractAddress, setNftContractAddress] = useState('');
  const [tokenId, setTokenId] = useState('');
  const [offerAmount, setOfferAmount] = useState('');
  const [formData, setFormData] = useState({ address: "", id: "", amount: ""});


  const handleSubmit = (event) => {
    event.preventDefault();

    useEffect(() => {
        createOffer();
    }, [formData]);
    // props.onSubmit(formData);
  };



  return (
    <form onSubmit={handleSubmit}>
      <label>
        NFT Contract Address:
        <input type="text" value={nftContractAddress} onChange={(e) => setNftContractAddress(e.target.value)} />
      </label>
      <br />
      <label>
        Token ID:
        <input type="text" value={tokenId} onChange={(e) => setTokenId(e.target.value)} />
      </label>
      <br />
      <label>
        Offer Amount:
        <input type="text" value={offerAmount} onChange={(e) => setOfferAmount(e.target.value)} /> 
      </label>
      <br />
      <button type="submit" onClick={() => setFormData({...formData, address: nftContractAddress, id: tokenId, amount: offerAmount })}>Make Offer</button>
    </form>
  );

}

export default Form;
