import React, { useState, useContext, useEffect } from 'react';
import Web3 from 'web3';

const DataContext = React.createContext();


export function web3instance () {
    const network = "mainnet";
    const infuraApi = "28d9ffd80cb04b8cb488e64060f0e3fe";
    const web3 = new Web3(Web3.providers.HttpProvider(`https://${network}.infura.io/v3/${infuraApi}`));
    return web3;
}

export function wethTokenAddress () {
    return "0xC02aaA39b223FE8D0A0e5C4F27eAD9083C756Cc2";
}

export function osApiKey () {
    return "d6a557dceac44623aad26ec5f7bf01e4";
}

export function networkName () {
    return "mainnet";
}








export function DataProvider ({ children }) {


    return (
        <DataContext.Provider >
            {children}
        </DataContext.Provider>
    )
}
